import React, { useState } from "react";
import { useStateValue } from "../Stateprovider";
import styles from "./Login.module.css";

const Login = () => {
  const [userEmail, setUserEmail] = useState("");
  const [userPass, setUserPass] = useState("");
  const [signUP, setSignUp] = useState(false);
  const [{ loggedIn }, dispatch] = useStateValue();

  const handleAuth = () => {};

  return (
    <div className={styles.container}>
      <span className={styles.login}> Hi There, Login or Sign Up</span>
      <div className={styles.inputDiv}>
        <span className={styles.inputHeading}> Email</span>
        <input
          type="text"
          placeholder="................."
          onChange={(e) => {
            setUserEmail(e.target.value);
          }}
          onClick={handleAuth}
        ></input>
      </div>
      <div className={styles.inputDiv}>
        <span className={styles.inputHeading}> Password </span>
        <input
          type="text"
          placeholder="********"
          onChange={(e) => {
            setUserPass(e.target.value);
          }}
          onClick={handleAuth}
        ></input>
      </div>
      <button classname={styles.btn} type="submit">
        {signUP ? "Sign Up" : "Sign In"}
      </button>
      <span className={styles.inputHeading}>
        New Here ? <span className={styles.redirect} onClick = {() => {setSignUp()}}>Sign Up</span>
      </span>
    </div>
  );
};

export default Login;
