import { useState } from "react"
import classes from  "./Hero.module.css"
export const Hero = () => {
    const [search, setSearch] = useState(false)
    const showSearch = () => {
        setSearch(!search)
    }

  return (
    <div className={classes.hero}>
        <div className={classes.nav}>
            <span>HouseWorth</span>
            <ul>
                <li href= "#explore">Explore</li>
                <li onClick={showSearch}>Search </li>
                <li>Sign In as agent</li>
            </ul>
        </div>
        <div className={classes.filterButtons}>
  <button
    className={activeFilter === "all" ? classes.active : ""}
    onClick={() => setActiveFilter("all")}
  >
    All
  </button>
  <button
    className={activeFilter === "2bedrooms" ? classes.active : ""}
    onClick={() => setActiveFilter("2bedrooms")}
  >
    2 Bedrooms
  </button>
  <button
    className={activeFilter === "3bedrooms" ? classes.active : ""}
    onClick={() => setActiveFilter("3bedrooms")}
  >
    3 Bedrooms
  </button>
</div>

    </div>
  )
}
