import { useState } from "react";
import classes from "./Hero.module.css";
export const Hero = () => {
  const [search, setSearch] = useState(false);
  const showSearch = () => {
    setSearch(!search);
  };

  return (
    <div className={classes.hero}>
      <div className={classes.nav}>
        <span>House Worth</span>
        <ul>
          <li>Explore</li>
          <li onClick={showSearch}>Search </li>
          <li>Sign In as agent</li>
        </ul>
      </div>
      <div className={classes.searchBar} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
  { search && (
    <div style={{ position: 'relative', display: 'flex', alignItems: 'center' }}>
      <input placeholder="Search ....." style={{ fontSize: '4.5rem', padding: '0px 80px', marginRight: '10px' }} />
      <button style={{ fontSize: '1.5rem', padding: '10px 20px' }}>Search</button>
    </div>
  )}
</div>

    </div>
  );
};
