import styles from "./Search.module.css";
import rowStyles from "./SearchRow.module.css";

import React, { useState } from "react";

const [numBath, setNumBath] = useState(1);
const [numBed, setNumBed] = useState(1);
const [maxPrice, setMaxPrice] = useState(1);
const [maxMiles, setMaxMiles] = useState(1);

const SearchRow = () => {
  return (
    <div className={rowStyles.row}>
      <div className={rowStyles.searchOption}>
        <span>Number of Bedroom</span>
        <input
          type="text"
          placeholder="2"
          onChange={(e) => {
            setNumBed(e.target.value);
          }}
        />
      </div>
      <div className={rowStyles.searchOption}>
        <span>Number of Bathrooms</span>
        <input
          type="text"
          placeholder="1"
          onChange={(e) => {
            setNumBath(e.target.value);
          }}
        />
      </div>
      <div className={rowStyles.searchOption}>
        <span>Max rent</span>
        <input
          type="text"
          placeholder="3000"
          onChange={(e) => {
            setMaxPrice(e.target.value);
          }}
        />
      </div>
      <div className={rowStyles.searchOption}>
        <span>Max Distance</span>
        <input
          type="text"
          placeholder="miles"
          onChange={(e) => {
            setMaxMiles(e.target.value);
          }}
        />
      </div>
    </div>
  );
};
const Search = () => {
  const executeSearch = () => {
    console.log("Searching...");
    console.log(numBath);
  };
  return (
    <div className={styles.container}>
      <div className={styles.term}>
        <span> Fall </span>
        <span> Spring </span>
        <span> Jan </span>
        <span> Full Academic </span>
      </div>
      <SearchRow />
      <button
        type="submit"
        className={styles.searchButton}
        onClick={executeSearch}
      >
        Search
      </button>
    </div>
  );
};

export default Search;
