import styles from "./Search.module.css";
import rowStyles from "./SearchRow.module.css";
import axios from "axios";
import { useStateValue } from "../Stateprovider";

import React, { useEffect, useState } from "react";

const Search = () => {
  const [listings, setListings] = useState([]);
  const [term, setTerm] = useState(1);
  const [minNumBath, setMinNumBath] = useState(1);
  const [maxNumBath, setMaxNumBath] = useState(1);
  const [minNumBed, setMinNumBed] = useState(1);
  const [maxNumBed, setMaxNumBed] = useState(1);
  const [maxPrice, setMaxPrice] = useState(1);
  const [minPrice, setMinPrice] = useState(1);
  const [maxMiles, setMaxMiles] = useState(1);
  const [minMiles, setMinMiles] = useState(1);
  const [{ listing_result }, dispatch] = useStateValue();

  const execute = async () => {
    axios
      .get("http://localhost:3001/?", { params: { 
                    max_no_bath    :maxNumBath,
                    min_no_bath    :minNumBath,
                    min_no_rooms    :minNumBed,
                     max_no_rooms   :maxNumBed,
                      max_rent  :maxPrice,
                       min_rent :minPrice,
                       max_distance :maxMiles,
                       min_distance :minMiles,

       } })
      .then((res) => {
        dispatch({
          type: "UPDATE_LISTING",
          listing_result: res.data,
        });
        return res.data;
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className={styles.container}>
      <div className={styles.term}>
        <span
          onClick={() => {
            setTerm();
          }}
        >Fall
        </span>
        <span
          onClick={() => {
            setTerm();
          }}
        >
          Spring
        </span>
        <span
          onClick={() => {
            setTerm();
          }}
        >
          Jan
        </span>
        <span
          onClick={() => {
            setTerm();
          }}
        >
          {" "}
          Full Academic Year
        </span>
      </div>
      {/* <SearchRow /> */}

      <div className={rowStyles.row}>
        <div className={rowStyles.searchOption}>
          <span>Number of Bedrooms</span>
          <input
            type="text"
            placeholder="Min"
            onChange={(e) => {
              setMinNumBed(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Max"
            onChange={(e) => {
              setMaxNumBed(e.target.value);
            }}
          />
        </div>
        <div className={rowStyles.searchOption}>
          <span>Number of Bathrooms</span>
          <input
            type="text"
            placeholder="Min"
            onChange={(e) => {
              setMinNumBath(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Max"
            onChange={(e) => {
              setMaxNumBath(e.target.value);
            }}
          />
        </div>
        <div className={rowStyles.searchOption}>
          <span>Rent</span>
          <input
            type="text"
            placeholder="Min"
            onChange={(e) => {
              setMinPrice(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Max"
            onChange={(e) => {
              setMaxPrice(e.target.value);
            }}
          />
        </div>
        <div className={rowStyles.searchOption}>
          <span>Distance (Miles)</span>
          <input
            type="text"
            placeholder="Min"
            onChange={(e) => {
              setMaxMiles(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Max"
            onChange={(e) => {
              setMinMiles(e.target.value);
            }}
          />
        </div>
      </div>
      {/* End*/}
      <button type="submit" className={styles.searchButton} onClick={execute}>
        Search
      </button>
    </div>
  );
};

export default Search;
