import styles from "./Search.module.css";
import rowStyles from "./SearchRow.module.css";
import axios from "axios";
import { useStateValue } from "../Stateprovider";

import React, { useEffect, useState } from "react";

const Search = () => {
  const [listings, setListings] = useState([]);
  const [numBath, setNumBath] = useState(1);
  const [numBed, setNumBed] = useState(1);
  const [maxPrice, setMaxPrice] = useState(1);
  const [maxMiles, setMaxMiles] = useState(1);
  const [{ listing_result }, dispatch] = useStateValue();

  const execute = async () => {
    axios
      .get("http://localhost:3001/")
      .then((res) => {
        dispatch({
          type: "UPDATE_LISTING",
          listing_result: res.data,
        });
        return res.data;
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className={styles.container}>
      <div className={styles.term}>
        <span> Fall </span>
        <span> Spring </span>
        <span> Jan </span>
        <span> Full Academic </span>
      </div>
      {/* <SearchRow /> */}

      <div className={rowStyles.row}>
        <div className={rowStyles.searchOption}>
          <span>Number of Bedroom</span>
          <input
            type="text"
            placeholder="minimum"
            onChange={(e) => {
              setNumBed(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="maximum"
            onChange={(e) => {
              setNumBed(e.target.value);
            }}
          />
        </div>
        <div className={rowStyles.searchOption}>
          <span>Number of Bathrooms</span>
          <input
            type="text"
            placeholder="minimum"
            onChange={(e) => {
              setNumBath(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="maximum"
            onChange={(e) => {
              setNumBed(e.target.value);
            }}
          />
        </div>
        <div className={rowStyles.searchOption}>
          <span>Rent</span>
          <input
            type="text"
            placeholder="Minimum"
            onChange={(e) => {
              setMaxPrice(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Maximum"
            onChange={(e) => {
              setNumBed(e.target.value);
            }}
          />
        </div>
        <div className={rowStyles.searchOption}>
          <span>Max Distance</span>
          <input
            type="text"
            placeholder="miles"
            onChange={(e) => {
              setMaxMiles(e.target.value);
            }}
          />
          <input
            type="text"
            placeholder="2"
            onChange={(e) => {
              setNumBed(e.target.value);
            }}
          />
        </div>
      </div>
      {/* End*/}
      <button type="submit" className={styles.searchButton} onClick={execute}>
        Search
      </button>
    </div>
  );
};

export default Search;
